            pickers_file_path = fileId;
            dropbox_picker_file = null;
            platform = 'google';

            $("#box2 .divUploadFile").css('display', 'none');
            $(".url-link").css('display', 'none');
            $("#box2 #FileProgress").css('display', 'block');

            getFileData = data.docs[0];
