            });

            $('#circle-a').find('strong').html(100 + '%');

            setTimeout(function() {
                $('#FileProgress').hide();
                $('#mainUploaded').show();
            }, 1500);

            // console.log(data.docs[0] , data.docs[0].embedUrl , ext);

            gdImgLink = 'https://lh3.googleusercontent.com/d/' + fileId + '=w133-h100-p-k-nu?access_token=' +
                accessToken;

            $('#mainUploaded').find('img').attr('src', gdImgLink);

            file_to_upload = null;
            google_picker_file = data.docs[0];

        } else {
            alert("Please Upload Image File");
