function notifi_Show(type,msg) {
    if(type=="success"){
        $('#notifi-heading').html('Success');
        $('#notifi-detail').html(msg);
        $('#Outernotification').removeClass('warning');
        $('#Outernotification').css('display','flex');
        $('#Outernotification').addClass('success');
    }else if(type=="error"){
        $('#notifi-heading').html('Error');
        $('#Outernotification').removeClass('success');
        $('#notifi-detail').html(msg);
        $('#Outernotification').removeClass('warning');
        $('#Outernotification').css('display','flex');
    }else if(type=="warning"){
        $('#notifi-heading').html('Warning');
        $('#notifi-detail').html(msg);
        $('#Outernotification').removeClass('success');
        $('#Outernotification').css('display','flex');
        $('#Outernotification').addClass('warning');
    }
}
